document.addEventListener("DOMContentLoaded",function(){const a=localStorage.getItem("selectedLanguage");a&&(document.getElementById("language").value=a)}),document.getElementById("language").addEventListener("change",function(){const a=this.value;localStorage.setItem("selectedLanguage",a)});function attachLanguageToForms(){const a=localStorage.getItem("selectedLanguage")||"en";document.querySelectorAll("form").forEach(b=>{if(!b.querySelector("input[name=\"language\"]")){const c=document.createElement("input");c.type="hidden",c.name="language",c.value=a,b.appendChild(c)}})}document.querySelectorAll("form").forEach(a=>{a.addEventListener("submit",attachLanguageToForms)});